#![feature(test)]

extern crate test;
use test::Bencher;
extern crate distances;
use distances::numeric::euclidean;
use distances::string::levenshtein;

#[bench]
fn big_euclidean(b: &mut Bencher) {
    b.iter(|| {
        let n = 1_000_000;
        let x: Vec<f64> = vec![0.2; n];
        let y: Vec<f64> = vec![0.3; n];
        euclidean(&x, &y);
    });
}

#[bench]
fn big_levenshtein(b: &mut Bencher) {
    b.iter(|| {
        let x = "";
        let y = "";
        levenshtein(&x,&y);
    });
}
